mapper.select:
select * from (select A.*, rownum rnum,(select t.tblName from boardTbl t where t.tbl=A.tbl) tblName from(
select * from board where 1 = 1


		and tbl = #{tbl}







order by noticeNo desc, refNo desc, levelNo asc

) A) where rnum >= #{startRecord} and rnum <= #{lastRecord}

select * from(select b.*, LAG(no) OVER (order by no desc) preNo,LAG(subject) over (order by no desc) preSubject, LEAD(no) OVER (order by no desc) nxtNo, LEAD(subject) over (order by no desc) nxtSubject, (select t.tblName from boardTbl t where t.tbl=b.tbl) tblName from board b where 1 = 1
and tbl = #{tbl}
order by no desc) where no = #{no}

mapper.insert:
insert into board 
values(
seq_board.nextval, #{num}, #{tbl}, #{writer}, #{subject}, #{content}, #{email}, #{passwd},
#{refNo}, #{stepNo}, #{levelNo}, #{parentNo}, #{hit}, #{ip}, #{memberNo}, #{noticeNo},
#{secretGubun}, sysdate, #{attachInfo}
)
